import React, { Component } from 'react';
import '../index.css'
import '../Careers.css';
import  Axios  from "axios";


import {
    FormGroup,
    Form,
    Input,
    Row,
    Col,
    Button,
    UncontrolledCarousel
  } from "reactstrap";


  const items = [
    {
      src: require("../assets/img/contact/picture1.jpg"),
      altText: '',
      caption: '',
      header: ''
    },
    {
      src: require("../assets/img/contact/picture2.jpg"),
      altText: '',
      caption: '',
      header: ''
    }
  ];



export default class ContactUs extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            fullName: '',
            phone: '',
            message: '',
            disabled: false,
            regExEmail : /^[^@]+@[^\.]+\..+$/,
            regExPhone : /^\d{10}$/,
            emailSent: null
        }
      }


      handleEmailError = e =>{
        if(!this.state.email.match(this.state.regExEmail)){
            this.setState({
                email:""
            })
        }
      }

      handlePhoneError = e =>{
        if(!this.state.phone.match(this.state.regExPhone)){
            this.setState({
              phone:""
            })
        }
      }



    handleOnChangeEmail = e =>{
        this.setState({
            email: e.target.value
        })
    }
    handleOnChangeName = e =>{
        this.setState({
            fullName: e.target.value
        })
    }
    handleOnChangePhone = e =>{
        this.setState({
            phone: e.target.value
        })
    }
    handleOnChangeComments= e =>{
        this.setState({
            message: e.target.value
        })
    }

    handleOnSubmit = e =>{
        e.preventDefault();
        // console.log(this.state);
        console.log(e.target);

        this.setState({
            disabled: true
        });

            Axios.post('http://localhost:5000/users/sendQuery', 
            this.state,
            {
                "headers": {
                  'Content-Type': 'application/json',
                }
              })
            .then(res => {
                this.setState({
                    disabled: false,
                    emailSent: true
                });

            })
            .catch(err => {
                console.log(err);

                this.setState({
                    disabled: false,
                    emailSent: false
                });
            })




            this.resetForm();
     }   

    resetForm = () => {
        this.setState({
            email: '',
            fullName: '',
            phone: '',
            message: ''
        })
    }



    render() {

        return (
            <section className = "my-5">
                <div className= "container" style={{marginTop:"-25px"}}>
                <UncontrolledCarousel items={items} />
                <br></br>
                    <div className = "well well-sm">
                        <h3><strong> </strong></h3>
                        <div className = "row">
                            <div className = "col-md-8">
                                <iframe src="https://www.google.com/maps/d/u/0/embed?mid=11EooEpb5v7KrkAhDql4_ubHPeROiAkgO"  
                                    style = {{
                                     border: '0',
                                     width: '100%',
                                     height: '370px',
                                     frameborder: '0',
                                }}  allowFullScreen></iframe>
                            </div>
                            <div className = "col-md-4">
                                <h4> <strong> Write Us Your Query</strong></h4>
                                <Form id="myForm" onSubmit={this.handleOnSubmit}>

                                    <Row>
                                        <Col md="12">
                                            <FormGroup>
                                                <Input
                                                    id="exampleFormControlInput1"
                                                    placeholder="name@example.com"
                                                    type="email"
                                                    required
                                                    value={this.state.email}
                                                    onFocus={()=>{
                                                        this.setState({
                                                            emailSent: null
                                                        })
                                                    }}
                                                    onBlur={this.handleEmailError}
                                                    onChange={this.handleOnChangeEmail}
                                                />
                                            </FormGroup>
                                        </Col>
                                    </Row>

                                    <Row>
                                        <Col md="12">
